import React from 'react';

const CartItem=(props)=> {
    const { price, title, qty } = props.product;
    const{product,
    onIncreaseQuantity,
    onDecreaseQuantity,
    onDeleteQuantity} = props;
    return (
      <div className="cart-item">
        
        <div className="left-block">
          <img style={styles.image} />
        </div>
        <div className="right-block">
          <div style={ { fontSize: 25 } }>{title}</div>
          <div style={ { color: '#777' } }>Rs {price} </div>
          <div style={ { color: '#777' } }>Qty: {qty} </div>
          <div className="cart-item-actions">
            {/* Buttons */}
            <img
              alt="increase"
              className="action-icons"
              src="https://cdn-icons.flaticon.com/png/512/3303/premium/3303893.png?token=exp=1657552386~hmac=4729fb13f13549c2d201e9fc969ed3ab"
              onClick={() => onIncreaseQuantity(product)}
            />
            <img
              alt="decrease"
              className="action-icons"
              src="https://cdn-icons-png.flaticon.com/512/1828/1828906.png"
              onClick={()=> onDecreaseQuantity(product)}
            />
            <img
              alt="delete"
              className="action-icons"
              src="https://cdn-icons.flaticon.com/png/512/484/premium/484662.png?token=exp=1657552519~hmac=90c3beeaa33392f17ad38fb2f419d4d3"
              onClick={()=> onDeleteQuantity(product.id   )}
            />
          </div>
        </div>
      </div>
    );
  }


const styles = {
  image: {
    height: 110,
    width: 110,
    borderRadius: 4,
    background: '#ccc'
  }
}

export default CartItem;
